import { keccak256, SigningKey, toUtf8Bytes } from "ethers/utils";

import {
  signaturesToBytes,
  signaturesToBytesSortedBySignerAddress
} from "../../src/utils";

const privateKey =
  "0x0123456789012345678901234567890123456789012345678901234567890123";
const signingKey = new SigningKey(privateKey);

const signaturesAndDigests = ["sig1", "sig3", "sig2"].map(message => {
  const bytes = toUtf8Bytes(message);
  const digest = keccak256(bytes);
  const signature = signingKey.signDigest(digest);

  return {
    digest,
    signature
  };
});
const signatures = signaturesAndDigests.map(sad => sad.signature);
const digests = signaturesAndDigests.map(sad => sad.digest);

describe("Utils / signature", () => {
  it("can convert signatures to bytes", () => {
    expect(signaturesToBytes(...signatures)).toEqual(
      ""
    );
  });

  it("can convert signatures to sorted bytes", () => {
    expect(
      signaturesToBytesSortedBySignerAddress(digests[0], ...signatures)
    ).toEqual(
      ""
    );
  });
});
